INSERT INTO `clima_preguntas_abiertas` VALUES (1, NULL, '¿Qué es lo que más valoras de trabajar en tu área actualmente?', 1, 1, 1);
INSERT INTO `clima_preguntas_abiertas` VALUES (2, NULL, 'Si pudieras cambiar una sola cosa para mejorar tu experiencia de trabajo, ¿cuál sería y por qué?', 2, 1, 1);
INSERT INTO `clima_preguntas_abiertas` VALUES (5, NULL, '¿Qué debería mejorar tu jefe inmediato (o liderazgo del área) para apoyarte mejor en tu trabajo?', 3, 1, 1);
