INSERT INTO `clima_reactivos` VALUES (1, 1, 'Entiendo los objetivos prioritarios de mi área para este año.', 1, 1);
INSERT INTO `clima_reactivos` VALUES (2, 1, 'Mi trabajo contribuye de manera clara al propósito y resultados de la empresa.', 2, 1);
INSERT INTO `clima_reactivos` VALUES (3, 1, 'Las prioridades se comunican con claridad y se mantienen estables la mayor parte del tiempo.', 3, 1);
INSERT INTO `clima_reactivos` VALUES (4, 1, 'Sé cómo se medirá el éxito de mi trabajo (metas/indicadores/entregables).', 4, 1);
INSERT INTO `clima_reactivos` VALUES (5, 2, 'Conozco con claridad mis responsabilidades principales.', 1, 1);
INSERT INTO `clima_reactivos` VALUES (6, 2, 'Sé qué se espera de mí en términos de calidad y tiempos de entrega.', 2, 1);
INSERT INTO `clima_reactivos` VALUES (7, 2, 'Cuando hay cambios en mis actividades, se aclara el alcance y la prioridad.', 3, 1);
INSERT INTO `clima_reactivos` VALUES (8, 2, 'Tengo claridad sobre a quién acudir para resolver dudas o escalar problemas.', 4, 1);
INSERT INTO `clima_reactivos` VALUES (9, 3, 'Los roles entre áreas están bien delimitados para evitar duplicidad o retrabajo.', 1, 1);
INSERT INTO `clima_reactivos` VALUES (10, 3, 'La coordinación entre puestos (jefaturas/supervisión/operación) es efectiva.', 2, 1);
INSERT INTO `clima_reactivos` VALUES (11, 3, 'Los procesos clave están definidos y se siguen de manera consistente.', 3, 1);
INSERT INTO `clima_reactivos` VALUES (12, 3, 'Las decisiones se toman con criterios claros, no por improvisación.', 4, 1);
INSERT INTO `clima_reactivos` VALUES (13, 4, 'Mi jefe inmediato comunica expectativas claras sobre el desempeño.', 1, 1);
INSERT INTO `clima_reactivos` VALUES (14, 4, 'Recibo seguimiento oportuno y orientación cuando lo necesito.', 2, 1);
INSERT INTO `clima_reactivos` VALUES (15, 4, 'Puedo expresar inquietudes o riesgos con confianza y respeto.', 3, 1);
INSERT INTO `clima_reactivos` VALUES (16, 4, 'Mi jefe inmediato actúa con coherencia entre lo que dice y lo que hace.', 4, 1);
INSERT INTO `clima_reactivos` VALUES (17, 5, 'El trabajo bien hecho es reconocido en mi equipo.', 1, 1);
INSERT INTO `clima_reactivos` VALUES (18, 5, 'El reconocimiento se otorga de manera justa y basada en resultados.', 2, 1);
INSERT INTO `clima_reactivos` VALUES (19, 5, 'Se valoran los esfuerzos adicionales cuando la operación lo requiere.', 3, 1);
INSERT INTO `clima_reactivos` VALUES (20, 5, 'Me siento valorado(a) por las contribuciones que realizo.', 4, 1);
INSERT INTO `clima_reactivos` VALUES (21, 6, 'Tengo oportunidades para aprender y mejorar mis habilidades.', 1, 1);
INSERT INTO `clima_reactivos` VALUES (22, 6, 'Recibo retroalimentación útil para desarrollarme profesionalmente.', 2, 1);
INSERT INTO `clima_reactivos` VALUES (23, 6, 'Mi jefe inmediato impulsa mi crecimiento y aprendizaje.', 3, 1);
INSERT INTO `clima_reactivos` VALUES (24, 6, 'La empresa ofrece capacitación relevante para mi función.', 4, 1);
INSERT INTO `clima_reactivos` VALUES (25, 7, 'La información necesaria para mi trabajo llega completa y a tiempo.', 1, 1);
INSERT INTO `clima_reactivos` VALUES (26, 7, 'Los cambios relevantes se comunican con anticipación suficiente.', 2, 1);
INSERT INTO `clima_reactivos` VALUES (27, 7, 'Las instrucciones o solicitudes suelen ser claras y sin ambigüedades.', 3, 1);
INSERT INTO `clima_reactivos` VALUES (28, 7, 'La comunicación en mi equipo es abierta y respetuosa.', 4, 1);
INSERT INTO `clima_reactivos` VALUES (29, 8, 'Las áreas colaboran para resolver problemas y evitar retrabajos.', 1, 1);
INSERT INTO `clima_reactivos` VALUES (30, 8, 'Se respetan acuerdos y compromisos entre áreas.', 2, 1);
INSERT INTO `clima_reactivos` VALUES (31, 8, 'Existe disposición para apoyar a otras áreas cuando se requiere.', 3, 1);
INSERT INTO `clima_reactivos` VALUES (32, 8, 'Cuando hay desacuerdos, se gestionan con enfoque a soluciones.', 4, 1);
INSERT INTO `clima_reactivos` VALUES (33, 9, 'Cuento con herramientas y sistemas adecuados para realizar mi trabajo.', 1, 1);
INSERT INTO `clima_reactivos` VALUES (34, 9, 'Los sistemas que utilizo funcionan de forma estable.', 2, 1);
INSERT INTO `clima_reactivos` VALUES (35, 9, 'Tengo acceso a la información y recursos necesarios para cumplir mis entregables.', 3, 1);
INSERT INTO `clima_reactivos` VALUES (36, 9, 'Las fallas de herramientas/sistemas se atienden con oportunidad.', 4, 1);
INSERT INTO `clima_reactivos` VALUES (37, 10, 'Los procesos de trabajo facilitan la eficiencia (evitan burocracia innecesaria).', 1, 1);
INSERT INTO `clima_reactivos` VALUES (38, 10, 'Los tiempos de respuesta de áreas de soporte son adecuados.', 2, 1);
INSERT INTO `clima_reactivos` VALUES (39, 10, 'Se promueve la mejora continua para simplificar y hacer más efectivo el trabajo.', 3, 1);
INSERT INTO `clima_reactivos` VALUES (40, 10, 'Los problemas recurrentes se atienden de raíz, no solo de manera temporal.', 4, 1);
INSERT INTO `clima_reactivos` VALUES (41, 11, 'La carga de trabajo es razonable la mayor parte del tiempo.', 1, 1);
INSERT INTO `clima_reactivos` VALUES (42, 11, 'Puedo mantener un equilibrio razonable entre trabajo y vida personal.', 2, 1);
INSERT INTO `clima_reactivos` VALUES (43, 11, 'Me siento seguro(a) reportando riesgos o condiciones inseguras.', 3, 1);
INSERT INTO `clima_reactivos` VALUES (44, 11, 'En general, la empresa cuida el bienestar del personal.', 4, 1);
INSERT INTO `clima_reactivos` VALUES (45, 12, 'En mi área se promueve un trato respetuoso y profesional.', 1, 1);
INSERT INTO `clima_reactivos` VALUES (46, 12, 'Las reglas y políticas se aplican de manera consistente.', 2, 1);
INSERT INTO `clima_reactivos` VALUES (47, 12, 'Las decisiones que afectan al personal se toman de manera justa.', 3, 1);
INSERT INTO `clima_reactivos` VALUES (48, 12, 'Me siento tratado(a) con equidad y respeto en mi entorno de trabajo.', 4, 1);
